<?php	ini_set('display_errors', '1');	$title = 'Projects';	include ('includes/header.html');		$q = "SELECT id, name, completion, short_description, keywords FROM projects.projects WHERE 1";	$r = mysqli_query($dbc, $q); // Run the query.		mysqli_close($dbc);?>	<div class="bg">    	<img src="img/bg-img/a2.jpg" class="bg-img" alt="Background image" />    </div>    <div class="row hero">    	<div class="small-12 centered">        <h1 class="white-drop">Browse our projects</h1>        	<div style="height:10rem"></div>                                <?php                if ($r) {					echo '<ul class="list-projects-grid">';                    while ($row = mysqli_fetch_array($r, MYSQLI_ASSOC)) {						$link = "/projects/" . str_replace(' ', '-', $row['name']) . "/?proj-id=" . $row['id'] . "";						$thumbUrl = "/img/project-thumbs/" . str_replace(' ', '-', $row['name']) . ".jpg";                        echo '<li class="project-container">';						echo '<a href="' . $link . '">';						echo '<img src="' . $thumbUrl . '" alt="Project thumbnail" width="128" height="128" />';                        echo '<h2>' . ucwords($row['name']) . '</h2>';                        echo '<em>' . date("F Y", strtotime($row['completion']) ) . '</em>';                        echo '<p>' . $row['short_description'] . '</p>';						echo '</a>';                        echo '</li>';                    }					echo '</ul>';                } else {                    echo '<h2>No projects returned.</h2>';	                }                mysqli_free_result($r);                ?>          			<div class="clearfix"></div>        </div><!--END SMALL-12-->    </div><!--END ROW--><?php	include ('includes/footer.html');?>